insert IGNORE into turtlegame1.item (id, description, name, slot, type_id, rarity_id) values (1, 'Jajko, które jednym ruchem posyła statki na wodzie na samo dno.', 'Jajko sztormu', null, 1, 1), (2, 'Przyjrzyj się jajku, a zobaczysz jak w środku przelewa się gorąca ciecz niczym w wulkanie.', 'Jajko magmy', null, 1, 1), (3, 'Jesteś w stanie przysiąc, że wewnątrz porusza się wir.', 'Jajko tornado', null, 1, 1),
